// HeroDetail.js
import React, { useEffect, useState } from 'react';
import { fetchCharacterById, fetchComicsByHeroId } from './services/marvelService';
import { useParams } from 'react-router-dom';
import Spinner from './Spinner'; // ou LoadingLogo

const HeroDetail = () => {
  const { id } = useParams();
  const [hero, setHero] = useState(null);
  const [comics, setComics] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const getHeroDetails = async () => {
      try {
        const heroDetail = await fetchCharacterById(id);
        const heroComics = await fetchComicsByHeroId(id);

        setHero(heroDetail);
        setComics(heroComics);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching hero details:', error);
        setLoading(false);
      }
    };

    getHeroDetails();
  }, [id]);

  if (loading) {
    return <Spinner />; // ou <LoadingLogo />
  }

  if (!hero) {
    return <p>Hero not found!</p>;
  }

  return (
    <div>
      <div className="header-in">
        <div className="container">
          <div className="logo-left">
            <img src="assets/logo/Group.png" alt="" />
          </div>
          <div className="form-in">
            <input type="text" placeholder="Procure por heroi" />
          </div>
        </div>
      </div>

      <section id="detail-hero">
        <div className="container">
          <div className="left-detail">
            <h1>{hero.name}</h1>
            <p>{hero.description || 'No description available.'}</p>

            <div className="stats">
              <div className="details-hqs">
                <span>Quadrinhos</span>
                <div className="api-details">
                  <img src="assets/icones/book/Group.png" alt="" />
                  <h5>{hero.comics.available || 0}</h5>
                </div>
              </div>
              <div className="details-hqs">
                <span>Filmes</span>
                <div className="api-details">
                  <img src="assets/icones/video/Shape.png" alt="" />
                  <h5>{hero.movies || 0}</h5>
                </div>
              </div>
            </div>
            <div className="rating">Rating: <img src="assets/review/Group 4.png" alt="" /></div>
            <div>Ultimo quadrinho: <strong>13 fev 2020</strong></div>
          </div>

          <div className="righ-detail">
            <img src={`${hero.thumbnail.path}/portrait_uncanny.${hero.thumbnail.extension}`} alt={hero.name} />
          </div>
        </div>
      </section>

      <div className="last-hqs">
        <h4 className="container">Ãšltimos Lancamentos</h4>
        <div className="container">
          {comics.map((comic) => (
            <div className="hq-card" key={comic.id}>
              <img src={`${comic.thumbnail.path}/portrait_uncanny.${comic.thumbnail.extension}`} alt={comic.title} />
              <h4>{comic.title}</h4>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default HeroDetail;
