import React from 'react';
import './ListHeroes.css'; // Certifique-se de criar e incluir o CSS aqui

const heroes = [
  { id: 1, name: 'Star-Lord', imgSrc: 'assets/images/hero.png' },
  { id: 2, name: 'Star-Lord', imgSrc: 'assets/images/hero.png' },
  { id: 3, name: 'Star-Lord', imgSrc: 'assets/images/hero.png' },
  { id: 4, name: 'Star-Lord', imgSrc: 'assets/images/hero.png' }
];

function ListHeroes() {
  return (
    <section id="list-heroes">
      <div className="container">
        {heroes.map(hero => (
          <div key={hero.id} className="card-heroes">
            <img src={hero.imgSrc} alt={hero.name} />
            <div className="button-favorite">
              <h2>{hero.name}</h2>
              <img src="assets/icones/heart/Path.png" alt="Favorite" />
            </div>
          </div>
        ))}
      </div>
    </section>
  );
}

export default ListHeroes;
