import React, { useState } from 'react';
import FavoriteIcon from './FavoriteIcon'; // Certifique-se de ajustar o caminho
import './HeroesList.css'; // Adicione o CSS necessário

const HeroesList = ({ heroes }) => {
  const [favoriteHeroes, setFavoriteHeroes] = useState(new Set());

  const toggleFavorite = (heroId) => {
    setFavoriteHeroes(prevFavorites => {
      const updatedFavorites = new Set(prevFavorites);
      if (updatedFavorites.has(heroId)) {
        updatedFavorites.delete(heroId);
      } else {
        updatedFavorites.add(heroId);
      }
      return updatedFavorites;
    });
  };

  return (
    <section id="list-heroes">
      <div className="container">
        {heroes.map(hero => (
          <div key={hero.id} className="card-heroes">
            <img src={hero.imgSrc} alt={hero.name} />
            <div className="button-favorite">
              <h2>{hero.name}</h2>
              <FavoriteIcon
                isFavorite={favoriteHeroes.has(hero.id)}
                onClick={() => toggleFavorite(hero.id)}
              />
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

export default HeroesList;
