import React from 'react';
import './HeroesSection.css';
import heroi from './assets/icones/heroi/heroi.png';
import toggle from './assets/toggle/toggle.svg'; // Imagem para o toggle

const HeroesSection = ({ totalHeroes, sortHeroes, isSorted }) => {
  return (
    <section id="heroes">
      <div className="container">
        <div className="bar">
          <div className="total-heroes">
            <h4>Encontrados {totalHeroes} her√≥is</h4>
          </div>
          <div className="order-favorites">
            <h5 onClick={sortHeroes} className={isSorted ? 'active' : ''}>
              <img src={heroi} alt="Heroi" /> Ordenar por nome - {isSorted ? "Z/A" : "A/Z"}
            </h5>
            <div className={`toggle-action ${isSorted ? 'active' : ''}`}>
              <img src={toggle} alt="Toggle" />
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default HeroesSection;
