// src/components/ListHeroes.js
import React, { useEffect, useState } from 'react';
import { fetchCharacters } from './services/marvelService'; // Certifique-se de que o caminho está correto

const ListHeroes = () => {
  const [heroes, setHeroes] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const getHeroes = async () => {
      const fetchedHeroes = await fetchCharacters();
      // Formatar os dados para se ajustar à estrutura esperada
      const formattedHeroes = fetchedHeroes.map(hero => ({
        id: hero.id,
        name: hero.name,
        imgSrc: `${hero.thumbnail.path}/portrait_small.${hero.thumbnail.extension}`, // Use o formato de imagem desejado
      }));
      setHeroes(formattedHeroes);
      setLoading(false);
    };

    getHeroes();
  }, []);

  if (loading) {
    return <p>Loading heroes...</p>;
  }

  return (
    <section id="list-heroes">
      <div className="container">
        {heroes.map(hero => (
          <div key={hero.id} className="card-heroes">
            <img src={hero.imgSrc} alt={hero.name} />
            <div className="button-favorite">
              <h2>{hero.name}</h2>
              <img src="assets/icones/heart/Path.png" alt="Favorite" />
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

export default ListHeroes;
