// src/components/ListHeroes.js
import React, { useEffect, useState } from 'react';
import { fetchCharacters } from './services/marvelService';
import { FaHeart } from 'react-icons/fa'; // Importa o ícone de coração

const ListHeroes = () => {
  const [heroes, setHeroes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [favorites, setFavorites] = useState([]);

  useEffect(() => {
    const getHeroes = async () => {
      const fetchedHeroes = await fetchCharacters();
      // Formatar os dados para se ajustar à estrutura esperada
      const formattedHeroes = fetchedHeroes.map(hero => ({
        id: hero.id,
        name: hero.name,
        imgSrc: `${hero.thumbnail.path}/portrait_uncanny.${hero.thumbnail.extension}`,
      }));
      setHeroes(formattedHeroes);
      setLoading(false);
    };

    getHeroes();
  }, []);

  // Função para adicionar ou remover um personagem dos favoritos
  const toggleFavorite = (heroId) => {
    setFavorites((prevFavorites) => {
      if (prevFavorites.includes(heroId)) {
        // Remove do favoritos se já estiver lá
        return prevFavorites.filter(id => id !== heroId);
      } else if (prevFavorites.length < 5) {
        // Adiciona aos favoritos se ainda não estiver e houver espaço
        return [...prevFavorites, heroId];
      }
      return prevFavorites; // Não faz nada se a lista de favoritos já estiver cheia
    });
  };

  // Função para verificar se um personagem é favorito
  const isFavorite = (heroId) => favorites.includes(heroId);

  if (loading) {
    return <p>Carregando Heróis...</p>;
  }

  return (
    <section id="list-heroes">
      <div className="container">
        {heroes.map(hero => (
          <div
            key={hero.id}
            className={`card-heroes ${isFavorite(hero.id) ? 'favorited' : ''}`}
          >
            <img src={hero.imgSrc} alt={hero.name} />
            <div className="button-favorite" onClick={() => toggleFavorite(hero.id)}>
              <h2>{hero.name}</h2>
              <FaHeart
                color={isFavorite(hero.id) ? 'red' : 'gray'}
                size={24}
                className="favorite-icon"
              />
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

export default ListHeroes;
