// App.js
import React from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import ListHeroes from './ListHeroes';
import HeroDetail from './HeroDetail'; // Importe o componente de detalhes
import './App.css'; // Certifique-se de criar e incluir o CSS aqui

const App = () => (
  <Router>
    <Switch>
      <Route exact path="/" component={ListHeroes} />
      <Route path="/hero/:id" component={HeroDetail} /> {/* Rota para detalhes do her√≥i */}
    </Switch>
  </Router>
);

export default App;
