// src/components/HeroesSection.js
import React, { useState } from 'react';
import ListHeroes from './ListHeroes';

function HeroesSection() {
  const [sortOrder, setSortOrder] = useState('asc'); // Estado para a ordem de classificação

  const handleSortToggle = () => {
    setSortOrder(prevOrder => (prevOrder === 'asc' ? 'desc' : 'asc'));
  };

  return (
    <section id="heroes">
      <div className="container">
        <div className="bar">
          <div className="total-heroes">
            <h4>Encontrados 20 heróis</h4>
          </div>
          <div className="order-favorites">
            <h5>Ordenar por nome - {sortOrder === 'asc' ? 'A/Z' : 'Z/A'}</h5>
            <div className="toggle-action" onClick={handleSortToggle}>
              <img src="assets/toggle/Group 2.svg" alt="Toggle" />
            </div>
            <div className="favorites">
              <img src="assets/icones/heart/Path.png" alt="Favorites" /> Somente favoritos
            </div>
          </div>
        </div>
        <ListHeroes sortOrder={sortOrder} />
      </div>
    </section>
  );
}

export default HeroesSection;
