import axios from 'axios';

// Substitua com suas próprias chaves da API da Marvel
const PUBLIC_KEY = 'sua_chave_publica_aqui';
const PRIVATE_KEY = 'sua_chave_privada_aqui';

const BASE_URL = 'https://gateway.marvel.com:443/v1/public';
const TIMESTAMP = new Date().getTime();
const HASH = md5(TIMESTAMP + PRIVATE_KEY + PUBLIC_KEY); // Função para gerar o hash MD5

// Função para gerar o hash MD5
function md5(string) {
  return crypto.createHash('md5').update(string).digest('hex');
}

export const fetchCharacters = async () => {
  try {
    const response = await axios.get(`${BASE_URL}/characters`, {
      params: {
        apikey: PUBLIC_KEY,
        ts: TIMESTAMP,
        hash: HASH,
      }
    });
    return response.data.data.results;
  } catch (error) {
    console.error('Erro ao buscar personagens:', error);
    throw error;
  }
};

export const fetchCharacterDetails = async (characterId) => {
  try {
    const response = await axios.get(`${BASE_URL}/characters/${characterId}`, {
      params: {
        apikey: PUBLIC_KEY,
        ts: TIMESTAMP,
        hash: HASH,
      }
    });
    return response.data.data.results[0];
  } catch (error) {
    console.error('Erro ao buscar detalhes do personagem:', error);
    throw error;
  }
};

export const fetchComicsByCharacter = async (characterId) => {
  try {
    const response = await axios.get(`${BASE_URL}/characters/${characterId}/comics`, {
      params: {
        apikey: PUBLIC_KEY,
        ts: TIMESTAMP,
        hash: HASH,
      }
    });
    return response.data.data.results;
  } catch (error) {
    console.error('Erro ao buscar quadrinhos do personagem:', error);
    throw error;
  }
};
